mapper.select:
select * from onflight
where on_flight_id = #{onFlightID} and
      estimated_takeoff_time = #{takeoff}

mapper.update:
update onflight
type_id=#{onFlight.typeID}, ticket_type_id=#{onFlight.ticketTypeID}, current_status=#{onFlight.currentStatus}, estimated_takeoff_time=#{onFlight.estimatedTakeoffTime}, actual_takeoff_time=#{onFlight.actualTakeoffTime}, estimated_arrival_time=#{onFlight.estimatedArrivalTime}, actual_arrival_time=#{onFlight.actualArrivalTime}, boarding_gate=#{onFlight.boardingGate}
where on_flight_id = #{onFlightID} and estimated_takeoff_time = #{takeoff}

mapper.select:
select air_onflight.*,start_city,start_airport,start_terminal,end_city,end_airport,end_terminal
     ,ticket_pricing,discount,insurance_costs,ticket_count,remaining_tickets
from air_onflight left join air_start_airport on air_onflight.ticket_type_id = air_start_airport.ticket_type_id
    left join air_end_airport on air_start_airport.ticket_type_id = air_end_airport.ticket_type_id

    and start_city =#{startCityName}
    and end_city=#{endCityName}

select air_onflight.*,start_city,start_airport,start_terminal,end_city,end_airport,end_terminal
,ticket_pricing,discount,insurance_costs,ticket_count,remaining_tickets
from air_onflight left join air_start_airport on air_onflight.ticket_type_id = air_start_airport.ticket_type_id
left join air_end_airport on air_start_airport.ticket_type_id = air_end_airport.ticket_type_id

    and start_city =#{startCityName}
    and end_city=#{endCityName}
    and estimated_takeoff_time >= #{takeoff}
    and estimated_takeoff_time  <= #{arrive}
